.article-form input.hs-button.primary.large {
    background-color: #FFA077;
}

label {
    color:#8D93A8;
    font-size: 14px;
    font-family: montserrat;
    line-height: 20px;
}

.hs_error_rollup {
    position: absolute;
    margin-top: 100px;
}

.hs-input {
    background-color: #ffffff;
    border-radius: 5px;
    padding-left: 30px;
    padding-right: 30px;
    padding-top: 13px;
    padding-bottom: 13px;
    border: 1px solid #E5E5E5;
    min-width: 460px;
}

.hs-input:focus {
    outline-color : #24584D !important;
}

form {
